import React, { useState, useEffect } from "react";
import DashNav from "./DashNav";
import "./Dash.css";

function SellerDash() {
  const [products, setProducts] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Loading state for products
  const [error, setError] = useState(null); // Error state

  // Fetch the user from localStorage on component mount
  useEffect(() => {
    if (typeof window !== "undefined") {
      const storedUser = localStorage.getItem("foundUser");
      if (storedUser) {
        setUser(JSON.parse(storedUser));
      }
    }
  }, []);

  // Fetch products if the user is a seller
  useEffect(() => {
    if (user && user.role === "seller") {
      setLoading(true); // Start loading
      fetch(`/users/${user.id}/products`)
        .then((res) => {
          if (!res.ok) {
            throw new Error("Failed to fetch products");
          }
          return res.json();
        })
        .then((data) => {
          setProducts(data);
          setLoading(false); // Stop loading once data is fetched
        })
        .catch((err) => {
          setError(err.message); // Handle error
          setLoading(false); // Stop loading even on error
        });
    }
  }, [user]);

  // Display loading state while waiting for user or products
  if (!user || loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <header>
        <DashNav />
      </header>
      <main className="container mt-5">
        <h2 className="text-center mb-4">{user?.name}'s Dashboard</h2>

        {error && <p className="text-center text-danger">{error}</p>}

        <div className="row">
          {products.length ? (
            products.map((product) => (
              <div
                key={product.id}
                className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4"
              >
                <div className="card shadow-lg h-100 border-light rounded">
                  <img
                    src={product.image_url}
                    className="card-img-top"
                    alt={product.name}
                    style={{
                      height: "250px",
                      objectFit: "cover",
                      borderTopLeftRadius: "0.25rem",
                      borderTopRightRadius: "0.25rem",
                    }}
                  />
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title text-truncate">{product.name}</h5>
                    <p className="card-text text-muted text-truncate">
                      {product.description}
                    </p>
                    <h6 className="text-primary mt-auto">Price: {product.price}</h6>
                  </div>
                  <div className="card-footer text-center">
                    <button className="btn btn-outline-primary w-100">
                      Edit Product
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12">
              <p className="text-center text-muted">No products found</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default SellerDash;
