import React from "react";
import DashNav from "./DashNav";
import "./Dash.css";

function SellerDash() {
  const [products, setProducts] = React.useState([]);
  const [user, setUser] = React.useState(null); // State to store user data

  // Get the user from localStorage and set it in state
  React.useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (storedUser) {
      setUser(storedUser); // Set the user to state
    } else {
      console.log("No user found in localStorage");
    }
  }, []);

  // Fetch products for the seller after the user is set
  React.useEffect(() => {
    if (user && user.role.role === "seller") {
      fetch(`/seller-dashboard?user_id=${user.id}`)
        .then((res) => res.json())
        .then((data) => {
          console.log(data); // Log the response from the API
          setProducts(data); // Set the products in state
        })
        .catch((error) => {
          console.error("Error fetching products:", error);
        });
    }
  }, [user]); // Trigger the effect when the user state is updated

  return (
    <div>
      <header>
        <DashNav />
      </header>
      <main className="container mt-5">
        <h2 className="text-center mb-4">{user?.username}'s Dashboard</h2>

        <div className="row">
          {products.length ? (
            products.map((product) => (
              <div key={product.id} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
                <div className="card shadow-lg h-100 border-light rounded">
                  <img
                    src={product.image_url}
                    className="card-img-top"
                    alt={product.name}
                    style={{
                      height: "250px",
                      objectFit: "cover",
                      borderTopLeftRadius: "0.25rem",
                      borderTopRightRadius: "0.25rem",
                    }}
                  />
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title text-truncate">{product.name}</h5>
                    <p className="card-text text-muted text-truncate">{product.description}</p>
                    <h6 className="text-primary mt-auto">Price: {product.price}</h6>
                  </div>
                  <div className="card-footer text-center">
                    <button className="btn btn-outline-primary w-100">Edit Product</button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12">
              <p className="text-center text-muted">No products found</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default SellerDash;
